import {DecodeError} from '@codec-bytes/error';

export default class Base32DecodeError extends DecodeError {
	constructor(reason, object, position) {
		super('base32', reason, object, position);
	}
}

import {EncodeError} from '@codec-bytes/error';

export default class Base32EncodeError extends EncodeError {
	constructor(reason, object, position) {
		super('base32', reason, object, position);
	}
}

import {iter} from '@iterable-iterator/iter';
import {next, StopIteration} from '@iterable-iterator/next';
import {ValueError} from '@failure-abstraction/error';

import byte5tochar8 from './byte5tochar8.js';
import byte4tochar7 from './byte4tochar7.js';
import byte3tochar5 from './byte3tochar5.js';
import byte2tochar4 from './byte2tochar4.js';
import byte1tochar2 from './byte1tochar2.js';

import Base32DecodeError from './Base32DecodeError.js';

import variants from './variants.js';
import DEFAULT_OPTIONS from './DEFAULT_OPTIONS.js';

export default function* _decode(bytes, options = DEFAULT_OPTIONS) {
	if (options.variant) {
		if (Object.prototype.hasOwnProperty.call(variants, options.variant)) {
			options = variants[options.variant];
		} else {
			throw new ValueError(`unknown Base32 variant ${options.variant}`);
		}
	}

	let start = 0;

	const {alphabet, padding} = options;

	const it = iter(bytes);

	while (true) {
		let a;
		let b;
		let c;
		let d;
		let e;

		try {
			a = next(it);
		} catch (error) {
			if (error instanceof StopIteration) break;
			else throw error;
		}

		if (a < 0x00 || a > 0xff) {
			const reason = `byte out of range 0x00 <= ${a} <= 0xFF`;
			const position = {start, end: start + 1};
			throw new Base32DecodeError(reason, bytes, position);
		}

		++start;

		try {
			b = next(it);
		} catch (error) {
			if (error instanceof StopIteration) {
				yield* byte1tochar2(alphabet, a);
				if (padding) {
					yield padding;
					yield padding;
					yield padding;
					yield padding;
					yield padding;
					yield padding;
				}

				break;
			} else throw error;
		}

		if (b < 0x00 || b > 0xff) {
			const reason = `byte out of range 0x00 <= ${b} <= 0xFF`;
			const position = {start, end: start + 1};
			throw new Base32DecodeError(reason, bytes, position);
		}

		++start;

		try {
			c = next(it);
		} catch (error) {
			if (error instanceof StopIteration) {
				yield* byte2tochar4(alphabet, a, b);
				if (padding) {
					yield padding;
					yield padding;
					yield padding;
					yield padding;
				}

				break;
			} else throw error;
		}

		if (c < 0x00 || c > 0xff) {
			const reason = `byte out of range 0x00 <= ${c} <= 0xFF`;
			const position = {start, end: start + 1};
			throw new Base32DecodeError(reason, bytes, position);
		}

		++start;

		try {
			d = next(it);
		} catch (error) {
			if (error instanceof StopIteration) {
				yield* byte3tochar5(alphabet, a, b, c);
				if (padding) {
					yield padding;
					yield padding;
					yield padding;
				}

				break;
			} else throw error;
		}

		if (d < 0x00 || d > 0xff) {
			const reason = `byte out of range 0x00 <= ${d} <= 0xFF`;
			const position = {start, end: start + 1};
			throw new Base32DecodeError(reason, bytes, position);
		}

		++start;

		try {
			e = next(it);
		} catch (error) {
			if (error instanceof StopIteration) {
				yield* byte4tochar7(alphabet, a, b, c, d);
				if (padding) {
					yield padding;
				}

				break;
			} else throw error;
		}

		if (e < 0x00 || e > 0xff) {
			const reason = `byte out of range 0x00 <= ${e} <= 0xFF`;
			const position = {start, end: start + 1};
			throw new Base32DecodeError(reason, bytes, position);
		}

		yield* byte5tochar8(alphabet, a, b, c, d, e);

		++start;
	}
}

import {iter} from '@iterable-iterator/iter';
import {next, StopIteration} from '@iterable-iterator/next';
import {enumerate} from '@iterable-iterator/zip';
import {toObject, inverse} from '@iterable-iterator/mapping';
import {ValueError} from '@failure-abstraction/error';

import char8tobyte5 from './char8tobyte5.js';
import char7tobyte4 from './char7tobyte4.js';
import char5tobyte3 from './char5tobyte3.js';
import char4tobyte2 from './char4tobyte2.js';
import char2tobyte1 from './char2tobyte1.js';

import Base32EncodeError from './Base32EncodeError.js';

import variants from './variants.js';
import DEFAULT_OPTIONS from './DEFAULT_OPTIONS.js';

export default function* _encode(string, options = DEFAULT_OPTIONS) {
	if (options.variant) {
		if (Object.prototype.hasOwnProperty.call(variants, options.variant)) {
			options = variants[options.variant];
		} else {
			throw new ValueError(`unknown Base32 variant ${options.variant}`);
		}
	}

	let start = 0;

	const index = options.index || toObject(inverse(enumerate(options.alphabet)));
	const padding = options.padding;

	const it = iter(string);

	while (true) {
		const digits = [];

		let stop = 0;

		do {
			try {
				digits.push(next(it));
			} catch (error) {
				if (error instanceof StopIteration) break;
				else throw error;
			}
		} while (++stop < 8);

		if (stop === 0) break;

		if (padding) {
			stop = digits.indexOf(padding);
			stop = stop === -1 ? digits.length : stop;
			if (stop === 0) {
				const reason = `padding at wrong position in ${digits.join('')}`;
				const position = {start: start + stop, end: start + stop + 1};
				throw new Base32EncodeError(reason, string, position);
			} else if (stop === 2 || stop === 4 || stop === 5 || stop === 7) {
				for (let i = stop + 1; i < digits.length; ++i) {
					if (digits[i] !== padding) {
						const reason = `incorrect padding end in ${digits.join('')}`;
						const position = {start: start + i, end: start + i + 1};
						throw new Base32EncodeError(reason, string, position);
					}
				}

				if (digits.length !== 8) {
					const reason = `missing padding in ${digits.join('')}`;
					const position = {start, end: start + digits.length};
					throw new Base32EncodeError(reason, string, position);
				}
			}
		}

		const mask = [0, 3, 0, 15, 1, 0, 7, 0];

		for (let i = 0; i < stop; ++i) {
			const digit = digits[i];

			if (!Object.prototype.hasOwnProperty.call(index, digit)) {
				const reason = `not in alphabet ${digit}`;
				const position = {start: start + i, end: start + i + 1};
				throw new Base32EncodeError(reason, string, position);
			}

			// A1 a2 a3 a4 a5 b1 b2 b3
			// b4 b5 c1 c2 c3 c4 c5 d1
			// d2 d3 d4 d5 e1 e2 e3 e4
			// e5 f1 f2 f3 f4 f5 g1 g2
			// g3 g4 g5 h1 h2 h3 h4 h5
			else if (i + 1 === stop && index[digit] & mask[i]) {
				const reason = `trailing bits in ${digits
					.slice(0, i + 1)
					.join('')}, last digit should be ${
					options.alphabet[index[digit] & ~mask[i]]
				}`;
				const position = {start: start + i, end: start + i + 1};
				throw new Base32EncodeError(reason, string, position);
			}
		}

		const [a, b, c, d, e, f, g, h] = digits;
		let reason;
		let position;
		// eslint-disable-next-line default-case
		switch (stop) {
			case 8:
				yield* char8tobyte5(index, a, b, c, d, e, f, g, h);
				start += 8;
				continue;
			case 7:
				yield* char7tobyte4(index, a, b, c, d, e, f, g);
				break;
			case 6:
				reason = `unterminated byte sequence ${a}${b}${c}${d}${e}${f}`;
				position = {start, end: start + 6};
				throw new Base32EncodeError(reason, string, position);
			case 5:
				yield* char5tobyte3(index, a, b, c, d, e);
				break;
			case 4:
				yield* char4tobyte2(index, a, b, c, d);
				break;
			case 3:
				reason = `unterminated byte sequence ${a}${b}${c}`;
				position = {start, end: start + 3};
				throw new Base32EncodeError(reason, string, position);
			case 2:
				yield* char2tobyte1(index, a, b);
				break;
			case 1:
				reason = `unterminated byte sequence ${a}`;
				position = {start, end: start + 1};
				throw new Base32EncodeError(reason, string, position);
		}

		if (padding) {
			let extra;

			try {
				extra = next(it);
			} catch (error) {
				if (error instanceof StopIteration) break;
				else throw error;
			}

			reason = `input continues after padding ${extra}`;
			position = {start: start + 8, end: start + 9};

			throw new Base32EncodeError(reason, string, position);
		} else break;
	}
}

export default function* byte1tochar2(alphabet, a) {
	// A1 a2 a3 a4 a5 | a6 a7 a8 0 0
	yield alphabet[a >> 3];
	yield alphabet[31 & (a << 2)];
}

export default function* byte2tochar4(alphabet, a, b) {
	// A1 a2 a3 a4 a5 | a6 a7 a8 b1 b2 | b3 b4 b5 b6 b7 | b8 0 0 0 0
	yield alphabet[a >> 3];
	yield alphabet[(31 & (a << 2)) | (b >> 6)];
	yield alphabet[31 & (b >> 1)];
	yield alphabet[31 & (b << 4)];
}

export default function* byte3tochar5(alphabet, a, b, c) {
	// A1 a2 a3 a4 a5 | a6 a7 a8 b1 b2 | b3 b4 b5 b6 b7 | b8 c1 c2 c3 c4 | c5 c6 c7 c8 0
	yield alphabet[a >> 3];
	yield alphabet[(31 & (a << 2)) | (b >> 6)];
	yield alphabet[31 & (b >> 1)];
	yield alphabet[(31 & (b << 4)) | (c >> 4)];
	yield alphabet[31 & (c << 1)];
}

export default function* byte4tochar7(alphabet, a, b, c, d) {
	// A1 a2 a3 a4 a5 | a6 a7 a8 b1 b2 | b3 b4 b5 b6 b7 | b8 c1 c2 c3 c4 | c5 c6 c7 c8 d1 | d2 d3 d4 d5 d6 | d7 d8 0 0 0
	yield alphabet[a >> 3];
	yield alphabet[(31 & (a << 2)) | (b >> 6)];
	yield alphabet[31 & (b >> 1)];
	yield alphabet[(31 & (b << 4)) | (c >> 4)];
	yield alphabet[(31 & (c << 1)) | (d >> 7)];
	yield alphabet[31 & (d >> 2)];
	yield alphabet[31 & (d << 3)];
}

export default function* byte5tochar8(alphabet, a, b, c, d, e) {
	// A1 a2 a3 a4 a5 | a6 a7 a8 b1 b2 | b3 b4 b5 b6 b7 | b8 c1 c2 c3 c4 | c5 c6 c7 c8 d1 | d2 d3 d4 d5 d6 | d7 d8 e1 e2 e3 | e4 e5 e6 e7 e8
	yield alphabet[a >> 3];
	yield alphabet[(31 & (a << 2)) | (b >> 6)];
	yield alphabet[31 & (b >> 1)];
	yield alphabet[(31 & (b << 4)) | (c >> 4)];
	yield alphabet[(31 & (c << 1)) | (d >> 7)];
	yield alphabet[31 & (d >> 2)];
	yield alphabet[(31 & (d << 3)) | (e >> 5)];
	yield alphabet[31 & e];
}

export default function* char2tobyte1(index, a, b) {
	// A1 a2 a3 a4 a5 b1 b2 b3
	yield (index[a] << 3) | (index[b] >> 2);
}

export default function* char4tobyte2(index, a, b, c, d) {
	// A1 a2 a3 a4 a5 b1 b2 b3
	// b4 b5 c1 c2 c3 c4 c5 d1
	yield (index[a] << 3) | (index[b] >> 2);
	yield (0xff & (index[b] << 6)) | (index[c] << 1) | (index[d] >> 4);
}

export default function* char5tobyte3(index, a, b, c, d, e) {
	// A1 a2 a3 a4 a5 b1 b2 b3
	// b4 b5 c1 c2 c3 c4 c5 d1
	// d2 d3 d4 d5 e1 e2 e3 e4
	yield (index[a] << 3) | (index[b] >> 2);
	yield (0xff & (index[b] << 6)) | (index[c] << 1) | (index[d] >> 4);
	yield (0xff & (index[d] << 4)) | (index[e] >> 1);
}

export default function* char7tobyte4(index, a, b, c, d, e, f, g) {
	// A1 a2 a3 a4 a5 b1 b2 b3
	// b4 b5 c1 c2 c3 c4 c5 d1
	// d2 d3 d4 d5 e1 e2 e3 e4
	// e5 f1 f2 f3 f4 f5 g1 g2
	yield (index[a] << 3) | (index[b] >> 2);
	yield (0xff & (index[b] << 6)) | (index[c] << 1) | (index[d] >> 4);
	yield (0xff & (index[d] << 4)) | (index[e] >> 1);
	yield (0xff & (index[e] << 7)) | (index[f] << 2) | (index[g] >> 3);
}

export default function* char8tobyte5(index, a, b, c, d, e, f, g, h) {
	// A1 a2 a3 a4 a5 b1 b2 b3
	// b4 b5 c1 c2 c3 c4 c5 d1
	// d2 d3 d4 d5 e1 e2 e3 e4
	// e5 f1 f2 f3 f4 f5 g1 g2
	// g3 g4 g5 h1 h2 h3 h4 h5
	yield (index[a] << 3) | (index[b] >> 2);
	yield (0xff & (index[b] << 6)) | (index[c] << 1) | (index[d] >> 4);
	yield (0xff & (index[d] << 4)) | (index[e] >> 1);
	yield (0xff & (index[e] << 7)) | (index[f] << 2) | (index[g] >> 3);
	yield (0xff & (index[g] << 5)) | index[h];
}

